  `path_type` char(80) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '路径标记',
  `size` bigint UNSIGNED NOT NULL DEFAULT 0 COMMENT '大小（单位b）',
